        private MethodDef decryptionMethod(Context krawk)
        {
            typ2 = new TypeDefUser(null, GenerateName(), krawk.ManifestModule.CorLibTypes.Object.TypeDefOrRef);
            krawk.ManifestModule.Import(typ2);
